import React, { useState, useEffect } from 'react';

import chiLogo from './pages/NFC/North/chiLogo.png';
import detLogo from './pages/NFC/North/detLogo.png';
import gnbLogo from './pages/NFC/North/gnbLogo.png';
import minLogo from './pages/NFC/North/minLogo.png';

import bufLogo from './pages/AFC/East/bufLogo.png';
import nweLogo from './pages/AFC/East/nweLogo.png';
import nyjLogo from './pages/AFC/East/nyjLogo.png';
import miaLogo from './pages/AFC/East/miaLogo.png';






function NewHome()
{
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    const url = "/home";

    const fetchData = async () => {
      try
      {
        const response = await fetch(url);
        const data = await response.json();
        setTeams(data);
      }
      catch (error)
      {
        console.log("error", error);
      }
    };

    fetchData();
  }, []);


  function setEachMatchup(team)
  {
    const homeTeam = team.slice(0,1);
    const awayTeam = team.slice(1);

    let matchup = ['',''];


    if (team.find(a => a.includes("Buf")))
    {
      if (awayTeam.find(b => b.includes("Buf")))
      {
        matchup[0] = bufLogo
      }
      else
      {
        matchup[1] = bufLogo
      }
    }


    return (matchup)

  }

  function setWeeklyMatchups()
  {
    return(
            teams.map(team =>
              <tr key={team.toString()}>
                <td className='matchups'>
                  {setEachMatchup(team)}
                </td>
              </tr>
            )
          )
  }


  return (
    <>
    <table className="allTeams">
      <tbody>

        <tr>
          <th >Week 1 Match ups</th>
        </tr>

        {setWeeklyMatchups()}

      </tbody>
    </table>
    </>
  );
}

export default NewHome;
