import React, { useState, useEffect } from 'react';
import './MatchupPage.css';
import predictions from './results.txt';


function MatchupPage()
{
  const [winner, setWinner] = useState('');
  const [loser, setLoser] = useState('');
  const [percent, setPercent] = useState('');


  useEffect(() => {
    const url = `/home/${window.location.pathname}`;


    const fetchData = async () => {
      try
      {
        // const response = await fetch(url);
        // const data = await response.json();

        const response = await fetch(predictions);
        let data = await response.text();
        data = data.split("\n");
        data = data.map((dataPoints) => dataPoints.split(","));

        const url = window.location.pathname.split("%20");
        let name = url[0];
        name = name.slice(1)

        const results = data.map(myFunction);

        function myFunction(value, index, array)
        {
          console.log(value,index);
          if ((name in value[0]) || (name in value[1]) || (name in value[2]))
          {
            return array[index];
          }
        }

        console.log(results);

        setWinner('winner');
        setLoser('data[1]');
        setPercent('data[2]');



      }
      catch (error)
      {
        console.log("error", error);
      }
    };

    fetchData();
  }, []);


  function goHome()
  {
    window.location.replace("/");
  }

  const currentURL = window.location.pathname;
  const teams = currentURL.split(",")

  let homeTeam = teams[0].split("%20");
  homeTeam = homeTeam.join(' ');
  homeTeam = homeTeam.slice(1)

  let awayTeam = teams[1].split("%20");
  awayTeam = awayTeam.join(' ')

  return (
    <>
    <h1>
      {awayTeam} @ {homeTeam}
    </h1>

    <table>
      <tbody>

        <tr>
          <td className="Winner">{percent}% chance of {winner} winning</td>
        </tr>

        <tr>
          <td className="Loser">Loser: {loser}</td>
        </tr>

      </tbody>
    </table>
    <div>
      <button className="matchupButton" onClick={goHome}>
        Back To Home Page
      </button>
    </div>
    </>
  );
}

export default MatchupPage;
